import logo from './logo.svg';
import React, {useState , useEffect} from 'react'
import './App.css';
import api from './Api'
import PokerForm from './components/PokerForm';
import { Container } from '@mui/material';
import { Button } from '@mui/joy';



function App() {
  const [games, setGames] = useState([])
  const [gameData, setGameData] = useState({game_name: ''})


  const fetchGames = async () => {
    const response = await api.get('/games/')
    setGames(response.data)
  }

  useEffect(() => {
    fetchGames()
  }, [])

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await api.post('/games/', gameData)
    fetchGames()
    setGameData({game_name: ''})
  }

  return (
    <div className="App">
      <header className="App-header">
      <h1>Poker Balance Tracker</h1>
      </header>
      <PokerForm/>
      <form onSubmit={handleFormSubmit}>
        <p>Game name</p>
        <input name="game_name" onChange={e => setGameData({...gameData, [e.target.name]: e.target.value})} value={gameData.game_name}/>
        <Button  type='submit'>
          sumbit
        </Button>
        {games.map(g => <p>Game {g.game_name} with id {g.id}</p>)}
      </form>
    </div>  
  );
}

export default App;
