import logo from './logo.svg';
import React, {useState , useEffect} from 'react'
import './App.css';
import api from './Api'
import PokerForm from './components/PokerForm';
import { Container } from '@mui/material';
import { Button } from '@mui/joy';
import TotalBalance from './components/TotalBalance';
import BasicModalDialog from './components/Modal'




function App() {
  const [games, setGames] = useState([])
  const [gameData, setGameData] = useState({game_name: '', players: []})
  const [playerGameData, setPlayerGameData] = useState({name: '', start_balance: 0, end_balance: 0})
  const [balanceData, setBalanceData] = useState({})


  const fetchGames = async () => {
    const response = await api.get('/games/')
    setGames(response.data)
  }

  const fetchBalanceDate = async () => {
    const response = await api.get('/players/')
    const data = {}
    for (const player of response.data) {
      data[player.player_name] = player.balance
    }
    setBalanceData(data)
  }

  useEffect(() => {
    fetchGames()
    fetchBalanceDate()
  }, [])



  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await api.post('/games/', gameData)
    fetchGames()
    setGameData({game_name: '', players: []})
  }

  const handleDeleteGames = async (event) => {
    event.preventDefault();
    const ids = games.map(g => g.id);

    for (const id of ids) {
      await api.delete(`/games/${id}`);
    }

    fetchGames();
  };


  return (
    <div className="App">
      <header className="App-header">
      <h1>Poker Balance Tracker</h1>
      </header>
      <TotalBalance balanceData={balanceData}/>
      <h2>Create Poker Game</h2>
      <BasicModalDialog gameData={gameData} setGameData={setGameData} handleSubmit={handleFormSubmit}/>
      <form onSubmit={handleFormSubmit}>
        <p>Game name</p>
        <input name="game_name" onChange={e => setGameData({...gameData, [e.target.name]: e.target.value})} value={gameData.game_name}/>
        <Button  type='submit'>
          sumbit
        </Button>
        {games.slice(-1).map(g => <p>Game {g.game_name} with id {g.id}</p>)}
      </form>
      <Button onClick={handleDeleteGames}>
        Delete games
      </Button>
    </div>  
  );
}

export default App;
