import React, { useState } from 'react';
import Input from '@mui/joy/Input';
import List from '@mui/joy/List';
import ListItem from '@mui/joy/ListItem';
import { IconButton, Sheet } from '@mui/joy';
import PlayerLine from './PlayerLine';


export default function PlayerBalance() {
    const [playersData, setPlayerData] = useState([{name: '', start_balance: '', end_balance: ''}])
    const addPlayer = () => {setPlayerData(prev => [...prev, {name: '', start_balance: '', end_balance: ''}])}


    return( <Sheet color='primary'>
            <List>
                {playersData.map((player, i) => {
                    return( i === playersData.length - 1 ? 
                    <ListItem>
                        <PlayerLine playerData={player} setPlayerData={setPlayerData} index={i} />
                        <IconButton variant="soft" color="primary" onClick={() => addPlayer()}>
                            <ion-icon name="add-outline" ></ion-icon>
                        </IconButton>
                    </ListItem> :  
                    <ListItem>
                        <PlayerLine playerData={player} setPlayerData={setPlayerData} index={i}/>
                    </ListItem>)
                })}
            </List>



        </Sheet>)
}
